import React, { Component } from 'react'

class MemeGenerator extends Component {
    constructor() {
        super()
        this.state = {
            topText: "",
            bottomText: "",
            randomImg: "http://i.imgflip.com/1bij.jpg",
            allMemeImg: []
        }

        this.handleChange = this.handleChange.bind(this)
    }

    componentDidMount() {
        fetch("https://api.imgflip.com/get_memes")
            .then(response => response.json())
            .then(response => {
                const { memes } = response.data
                console.log(memes[0])
                this.setState({
                    allMemeImg: memes
                })
            })
    }

    handleChange(event) {
        const { name, value } = event.target

        this.setState({
            [name]: value
        })
    }

    render() {
        return (
            <div>
                <form className="meme-form">
                    <input type="textbox" name="topText" value={this.setState.topText} onChange={this.handleChange} />
                    <input type="textbox" name="bottomText" value={this.setState.bottomText} onChange={this.handleChange} />
                    <button>Gen</button>
                </form>

                <h1>{this.state.topText} and {this.state.bottomText}</h1>
            </div>
        )
    }
}

export default MemeGenerator
