import React, { Component } from 'react'
import RandomSugestions from './RandomSuggestion'
import RandomSuggestion from './RandomSuggestion'

class MemeGenerator extends Component {
    constructor() {
        super()
        this.state = {
            topText: "",
            bottomText: "",
            randomImg: "http://i.imgflip.com/1bij.jpg",
            allMemeImg: [],
            sugestionImgOne: "",
            sugestionImgTwo: ""
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    componentDidMount() {
        fetch("https://api.imgflip.com/get_memes")
            .then(response => response.json())
            .then(response => {
                const { memes } = response.data
                this.setState({
                    allMemeImg: memes
                })
            })
    }

    handleChange(event) {
        const { name, value } = event.target

        this.setState({
            [name]: value
        })
    }

    handleSubmit(event) {
        event.preventDefault()
        const rndm = Math.floor(Math.random() * this.state.allMemeImg.length)
        const imgUrl = this.state.allMemeImg[rndm].url
        this.setState({
            randomImg: imgUrl,

        })
    }

    genRandomImg() {
        const rndmOne = Math.floor(Math.random() * this.state.allMemeImg.length)
        return this.state.allMemeImg[rndmOne].url

    }


    render() {
        return (
            <div className="grid">
                <form className="meme-form" onSubmit={this.handleSubmit}>
                    <input
                        type="textbox"
                        name="topText"
                        value={this.setState.topText}
                        onChange={this.handleChange}
                        placeholder="Top Text"
                    />
                    <input
                        type="textbox"
                        name="bottomText"
                        value={this.setState.bottomText}
                        onChange={this.handleChange}
                        placeholder="Bottom Text"
                    />
                    <button>Gen</button>
                </form>

                <div className="meme">
                    <img src={this.state.randomImg} alt="" />
                    <h2 className="top">{this.state.topText}</h2>
                    <h2 className="bottom">{this.state.bottomText}</h2>
                </div>

                <RandomSuggestion rndmImgOne={this.genRandomImg} randomImgTwo={this.genRandomImg} />
            </div>
        )
    }
}

export default MemeGenerator
