import React from 'react'
import DeleteButton from '../components/DeleteButton'

class TodoItem extends React.Component {

    render() {
        const checkStyle = () => {
            if (this.props.todo.completed) {
                return "line-through";
            }
        }

        const deleteButton = () => {
            if (this.props.todo.completed) {
                return <DeleteButton />
            }
        }

        return (
            <div className="todo-item">
                <input
                    className="checkbox"
                    type="checkbox"
                    checked={this.props.todo.completed}
                    onChange={() => this.props.handler(this.props.todo.id)}
                />
                <h3 className={checkStyle()}  >{this.props.todo.text}</h3>
                {deleteButton()}
            </div>
        )
    }
}

export default TodoItem
