import React from 'react'

class TodoItem extends React.Component {

    render() {
        const checkStyle = { textDecoration: lineThrough }

        return (
            <div className="todo-item">
                <input
                    className="checkbox"
                    type="checkbox"
                    checked={this.props.todo.completed}
                    onChange={() => this.props.handler(this.props.todo.id)}
                />
                <h3 style={checkStyle}  >{this.props.todo.text}</h3>
            </div>
        )
    }
}

export default TodoItem
